import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { GoogleLogin } from 'react-google-login';

const socket = io('http://localhost:5000');

function App() {
  const [tasks, setTasks] = useState([]);
  const [email, setEmail] = useState('');
  const [newTask, setNewTask] = useState('');

  const fetchTasks = async () => {
    const res = await axios.get(`http://localhost:5000/tasks/${email}`);
    setTasks(res.data);
  };

  const addTask = async () => {
    await axios.post('http://localhost:5000/tasks', {
      title: newTask,
      status: 'in progress',
      owner: email,
      sharedWith: [],
    });
    setNewTask('');
    fetchTasks();
  };

  useEffect(() => {
    socket.on('task-updated', fetchTasks);
    return () => socket.off('task-updated');
  }, [email]);

  const handleLogin = (res) => {
    setEmail(res.profileObj.email);
    fetchTasks();
  };

  return (
    <div>
      {!email ? (
        <GoogleLogin
          clientId="774206678672-10l306vftpo159jqq90489buovne94si.apps.googleusercontent.com"
          onSuccess={handleLogin}
          cookiePolicy={'single_host_origin'}
        />
      ) : (
        <>
          <h2>Welcome: {email}</h2>
          <input
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="New task"
          />
          <button onClick={addTask}>Add Task</button>
          <ul>
            {tasks.map((task) => (
              <li key={task._id}>{task.title} ({task.status})</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
