import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');

  const handleSuccess = (credentialResponse) => {
    const token = credentialResponse.credential;
    const decoded = jwtDecode(token); // contains email, name, picture
    console.log('‚úÖ Login Success:', decoded);
    setUser(decoded);
  };

  const handleError = () => {
    console.log('‚ùå Login Failed');
  };

  const fetchTasks = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/tasks/${user.email}`);
      setTasks(res.data);
    } catch (err) {
      console.error('‚ùå Failed to fetch tasks:', err);
    }
  };

  const addTask = async () => {
    if (!title.trim()) return;
    try {
      await axios.post('http://localhost:5000/tasks', {
        title,
        status: 'incomplete',
        owner: user.email,
        sharedWith: [],
      });
      setTitle('');
      fetchTasks();
    } catch (err) {
      console.error('‚ùå Failed to add task:', err);
    }
  };

  useEffect(() => {
    if (user) {
      fetchTasks();
    }
  }, [user]);

  return (
    <GoogleOAuthProvider clientId="774206678672-10l306vftpo159jqq90489buovne94si.apps.googleusercontent.com">
      <div style={{ padding: 20, fontFamily: 'Arial' }}>
        <h1>üìù Todo App</h1>

        {!user ? (
          <GoogleLogin onSuccess={handleSuccess} onError={handleError} />
        ) : (
          <div>
            <p>üëã Welcome, {user.name} ({user.email})</p>
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter task"
              style={{ marginRight: 10, padding: 5 }}
            />
            <button onClick={addTask}>Add Task</button>

            <ul style={{ marginTop: 20 }}>
              {tasks.map((task) => (
                <li key={task._id}>{task.title}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
