import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import './App.css'; // You‚Äôll create this file next

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');

  const handleSuccess = (credentialResponse) => {
    const token = credentialResponse.credential;
    const decoded = jwtDecode(token);
    setUser(decoded);
  };

  const handleError = () => {
    console.log('‚ùå Login Failed');
  };

  const fetchTasks = async () => {
    if (!user?.email) return;
    try {
      const res = await axios.get(`http://localhost:5000/tasks/${user.email}`);
      setTasks(res.data);
    } catch (err) {
      console.error('‚ùå Failed to fetch tasks:', err);
    }
  };

  const addTask = async () => {
    if (!title.trim()) return;
    try {
      await axios.post('http://localhost:5000/tasks', {
        title,
        status: 'incomplete',
        owner: user.email,
        sharedWith: [],
      });
      setTitle('');
      fetchTasks();
    } catch (err) {
      console.error('‚ùå Failed to add task:', err);
    }
  };

  const logout = () => {
    setUser(null);
    setTasks([]);
    setTitle('');
  };


const deleteTask = async (id) => {
  try {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    fetchTasks(); // refresh the task list
  } catch (err) {
    console.error('‚ùå Failed to delete task:', err);
  }
};

  useEffect(() => {
    if (user) {
      fetchTasks();
    }
  }, [user]);

  return (
    <GoogleOAuthProvider clientId="774206678672-10l306vftpo159jqq90489buovne94si.apps.googleusercontent.com">
      <div className="app">
        <h1 className="title">üìù Todo Task Manager</h1>

        {!user ? (
          <GoogleLogin onSuccess={handleSuccess} onError={handleError} />
        ) : (
          <div className="card">
            <div className="user-info">
              <img src="360_F_65772719_A1UV5kLi5nCEWI0BNLLiFaBPEkUbv5Fv.jpg" alt="profile" className="avatar" />
              <div>
                <h2>{user.name}</h2>
                <p>{user.email}</p>
              </div>
              <button className="logout" onClick={logout}>Logout</button>
            </div>

            <div className="task-input">
              <input
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="‚úçÔ∏è Write your task..."
              />
              <button onClick={addTask}>Add</button>
            </div>

            <ul className="task-list">
  {tasks.map((task) => (
    <li key={task._id} className="task-item">
      <span>{task.title}</span>
      <button className="delete-btn" onClick={() => deleteTask(task._id)}>üóëÔ∏è</button>
    </li>
  ))}
</ul>

          </div>
        )}
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
